package Jv02_api;

public class Java08_MathCeil {

	public Java08_MathCeil() {
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.ceil(5.1));
		System.out.println(Math.ceil(-5.4));
		int n = (int)Math.round(Math.ceil(-5.4));
		System.out.println(n);
		System.out.println(Math.floor(8.9)+", "+Math.floor(-3.2));
		System.out.println(Math.sqrt(2));
		System.out.println((int)Math.pow(2.0, 3.0)); 
	}

}
